import { Link, NavLink } from 'react-router-dom';

function Header() {
  const clickStyle = { color: 'red' };

  const [isNavOpen, setIsNavOpen] = useState(false);

  const toggleNav = () => {
    setIsNavOpen(!isNavOpen);
  };

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      const offsetTop = section.offsetTop;
      window.scrollTo({
        top: offsetTop,
        behavior: 'smooth',
      });
      setIsNavOpen(false);
    }
  };

  return (
    <header>
      <meta
        name="viewport"
        content="width=device-width, initial-scale=1, maximum-scale=1, user-scalable=0"
      />
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css"
      />
      <div className="inner">
        <h1 className="logo">
          <NavLink exact to="/react_portfolio">
            Branden Min
          </NavLink>
        </h1>
        <div className="toggle-btn" onClick={toggleNav}>
          <i className={`fas ${isNavOpen ? 'fa-times' : 'fa-bars'}`}></i>
        </div>
        <ul className={`gnb ${isNavOpen ? 'open' : ''}`}>
          <a onClick={() => scrollToSection("body2")} to="/react_portfolio#body2">ABOUT</a>
          <a onClick={() => scrollToSection("projects")} to="/react_portfolio#projects">PROJECTS</a>
          <a onClick={() => scrollToSection("experiences")} to="/react_portfolio#experiences">EXPERIENCES</a>
          <a onClick={() => scrollToSection("skills")} to="/react_portfolio#skills">SKILLS</a>
          <a onClick={() => window.open('/react_portfolio/BrandenMin.pdf', '_black')}>CV</a>
        </ul>
        <div className="snsContact">
          <a href="https://www.linkedin.com/in/brandenmin/" target="_blank">
            <i className="fa-brands fa-linkedin"></i>
          </a>
          <a href="https://github.com/Gemnnn" target="_blank">
            <i className="fa-brands fa-github"></i>
          </a>
          <a href="#">
            <i className="fa-brands fa-facebook"></i>
          </a>
        </div>
      </div>
    </header>
  );
}

export default Header;
